usermeta.config:
{
  // View configuration: remove default stroke for cleaner appearance
  "view": {
    "stroke": "transparent"
  },
  // X-axis configuration
  "axisX": {
    "grid": false, // Disable grid lines for a cleaner look
    "offset": 3, // Offset to position the axis labels slightly away from the axis
    "labelAngle": 0, // Angle of the x-axis labels (horizontal)
    "labelFontSize": 14, // Font size of x-axis labels
    "titleFontSize": 18 // Font size of the x-axis title
  },
  // Y-axis configuration
  "axisY": {
    "grid": false, // Disable grid lines for a cleaner look
    "labelAngle": 0, // Angle of the y-axis labels (horizontal)
    "labelFontSize": 14, // Font size of y-axis labels
    "tickCount": 3, // Number of ticks on the y-axis for simplicity
    "titleFontSize": 18 // Font size of the y-axis title
  },
  // Legend configuration
  "legend": {
    "title": "", // No title for the legend
    "symbolSize": 180, // Size of the symbols in the legend
    "disable": false, // Ensure legend is enabled
    "labelFontSize": 14, // Font size of legend labels
    "labelFontWeight": "bold", // Bold font for legend labels
    "columnPadding": 180, // Padding between legend columns
    "labelColor": {
      "expr": "datum.label == 'Male' ? '#116e9d' : '#9d2993'" // Conditional color for labels
    },
    "orient": "top", // Position the legend at the top
    "labelAlign": "left" // Align legend text to the left of the symbol
  }
}
